package com.gyh.datasource;import com.alibaba.druid.pool.DruidDataSource;import org.springframework.boot.context.properties.ConfigurationProperties;import org.springframework.context.annotation.Bean;import org.springframework.context.annotation.Configuration;import org.springframework.context.annotation.Primary;import javax.sql.DataSource;/** * 注册一个 DruidDataSource */@Configurationpublic class DruidDataSourceConfig {    @Primary    @Bean    @ConfigurationProperties(prefix = "spring.datasource.druid")    public DataSource dataSource() {        DruidDataSource datasource = new DruidDataSource();        return datasource;    }}